--
-- Structure de la table `discipline`
--

CREATE TABLE IF NOT EXISTS `discipline` (
  `id_discipline` int(11) NOT NULL AUTO_INCREMENT,
  `nom_discipline` varchar(300) COLLATE utf8_unicode_ci NOT NULL,
  `logo` varchar(500) COLLATE utf8_unicode_ci DEFAULT NULL,
  `nombre_cours` int(11) DEFAULT NULL,
  `id_forum_discipline` int(11) DEFAULT NULL,
  `description_discipline` longtext COLLATE utf8_unicode_ci,
  `date_creation` datetime DEFAULT NULL,
  PRIMARY KEY (`id_discipline`),
  KEY `IDX_75BEEE3F140206AB` (`id_forum_discipline`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=33 ;

--
-- Contenu de la table `discipline`
--

INSERT INTO `discipline` (`id_discipline`, `nom_discipline`, `logo`, `nombre_cours`, `id_forum_discipline`, `description_discipline`, `date_creation`) VALUES
(1, 'Oriente Object ', '1java.png', 12, NULL, '<p>La programmation orient&eacute;e objet (PO), ou programmation par objet, est un paradigme de programmation informatique &eacute;labor&eacute; par les Norv&eacute;giens Ole-Johan Dahl et Kristen Nygaard au d&eacute;but des ann&eacute;es 1960 et poursuivi par les travaux d&#39;Alan Kay dans les ann&eacute;es 1970. Il consiste en la d&eacute;finition et l&#39;interaction de briques logicielles appel&eacute;es objets ; un objet repr&eacute;sente un concept, une id&eacute;e ou toute entit&eacute; du monde physique, comme une voiture, une personne ou encore une page d&#39;un livre. Il poss&egrave;de une structure interne et un comportement, et il sait interagir avec ses pairs. Il s&#39;agit donc de repr&eacute;senter ces objets et leurs relations ; l&#39;interaction entre les objets via leurs relations permet de concevoir et r&eacute;aliser les fonctionnalit&eacute;s attendues, de mieux r&eacute;soudre le ou les probl&egrave;mes. D&egrave;s lors, l&#39;&eacute;tape de mod&eacute;lisation rev&ecirc;t une importance majeure et n&eacute;cessaire pour la POO. C&#39;est elle qui permet de transcrire les &eacute;l&eacute;ments du r&eacute;el sous forme virtuelle!</p>\n', '2016-04-06 00:00:00'),
(2, 'Android', 'android.png', 5, NULL, '<p style="text-align: justify;">Android, prononc&eAacute; andro&iuml;de, est un syst&egrave;me d&#39;exploitation mobile open source 2,3 bas&eacute; sur le noyau Linux et d&eacute;velopp&eacute; actuellement par Google. Le syst&egrave;me a d&#39;abord &eacute;t&eacute; con&ccedil;u pour les smartphones et tablettes tactiles, puis s&#39;est diversifi&eacute; dans les objets connect&eacute;s et ordinateurs comme les t&eacute;l&eacute;visions (Android TV), les voitures (Android Auto), les ordinateurs (Android-x86) et les smartwatch (Android Wear). Le syst&egrave;me a &eacute;t&eacute; lanc&eacute; en juin 2007 &agrave; la suite du rachat par Google en 2005 de la startup du m&ecirc;me nom4. En 2015, Android est le syst&egrave;me d&#39;exploitation le plus utilis&eacute; dans le monde avec plus de 80 % de parts de march&eacute; dans les smartphones5.</p>\n', '2016-05-15 00:00:00'),
(3, 'IOS', 'ios.png', 2, 4, '<p style="text-align: justify;">Android, prononc&eAacute; andro&iuml;de, est un syst&egrave;me d&#39;exploitation mobile open source 2,3 bas&eacute; sur le noyau Linux et d&eacute;velopp&eacute; actuellement par Google. Le syst&egrave;me a d&#39;abord &eacute;t&eacute; con&ccedil;u pour les smartphones et tablettes tactiles, puis s&#39;est diversifi&eacute; dans les objets connect&eacute;s et ordinateurs comme les t&eacute;l&eacute;visions (Android TV), les voitures (Android Auto), les ordinateurs (Android-x86) et les smartwatch (Android Wear). Le syst&egrave;me a &eacute;t&eacute; lanc&eacute; en juin 2007 &agrave; la suite du rachat par Google en 2005 de la startup du m&ecirc;me nom4. En 2015, Android est le syst&egrave;me d&#39;exploitation le plus utilis&eacute; dans le monde avec plus de 80 % de parts de march&eacute; dans les smartphones5.</p>\n', '2016-05-15 00:00:00'),
