(87, 27, 21, 'Exos de zeros', 'Bonjour les zéros.', '<p>&quot;La th&eacute;orie sans la pratique est vide&quot;, c&#39;est pourquoi ce n&#39;est pas tr&egrave;s utile les tutoriels s&#39;ils ne sont pas accompagn&eacute;s d&#39;exercices d&#39;application.<br />\r\n<br />\r\nsuite aux demandes de certains zeros, j&#39;ai d&eacute;cid&eacute; de cr&eacute;er ce topics pr&eacute;sentant des sujets de programmation en java afin de toujours progresser.<br />\r\n&nbsp;</p>\r\n', NULL, NULL, '2016-05-19 09:50:14', NULL, NULL, NULL, NULL),
(88, 10, 19, 'aa', 'az', '<p><img alt="indecision" src="http://127.0.0.1/MOOCV5/web/bundles/espritmooc/ckeditor/plugins/smiley/images/whatchutalkingabout_smile.png" style="height:23px; width:23px" title="indecision" /> cdfhgcnv,b</p>\r\n', NULL, NULL, '2016-11-30 14:53:42', NULL, 1, NULL, NULL);

